mapper.select:
SELECT distinct t1.id,t1.user_id as userId,t1.name,t3.name AS areaName,t1.intention,t1.`type`,t1.customer_type AS customerType,t4.name AS userName,t1.use_type as useType,t1.progress_num AS progressNum,t1.create_time as createTime
FROM customer t1
LEFT JOIN customer_contact t2 ON t1.id=t2.customer_id
LEFT JOIN `area` t3 ON t1.area_id=t3.Id
LEFT JOIN users t4 ON t1.user_id=t4.id
LEFT JOIN customer_progress t5 ON t1.id=t5.customer_id
where t1.status=0

  and t5.next_time=#{param1.nextDate}


  and t1.create_time between #{param1.startDate} and #{param1.endDate}


  and t1.lats_time= #{param1.lastDate}


  and t1.progress_num=#{param1.progressNum}


  and t2.phone=#{param1.phone}


  and t1.intention=#{param1.intention}


  and t1.type=#{param1.type}


  and t3.id=#{param1.areaId}


  and t1.name like concat('%',#{param1.name},'%')


  and t1.use_type = #{param1.useType}


  and t1.user_id in

    #{item,jdbcType=INTEGER}


order by t1.create_time desc

SELECT distinct t1.id,t1.user_id as userId,t1.name,t3.name AS areaName,t1.intention,t1.`type`,t1.customer_type AS customerType,t4.name AS userName,t1.use_type as useType,t1.progress_num AS progressNum,t1.create_time as createTime
FROM customer t1
LEFT JOIN customer_contact t2 ON t1.id=t2.customer_id
LEFT JOIN `area` t3 ON t1.area_id=t3.Id
LEFT JOIN users t4 ON t1.user_id=t4.id
LEFT JOIN customer_progress t5 ON t1.id=t5.customer_id
where t1.status=0 and t1.user_id=#{userId}

  and t5.next_time=#{param1.nextDate}


  and t1.create_time between #{param1.startDate} and #{param1.endDate}


  and t1.lats_time= #{param1.lastDate}


  and t1.progress_num=#{param1.progressNum}


  and t2.phone=#{param1.phone}


  and t1.intention=#{param1.intention}


  and t1.type=#{param1.type}


  and t3.id=#{param1.areaId}


  and t1.name like concat('%',#{param1.name},'%')


  and t1.use_type = #{param1.useType}

order by t1.create_time desc

SELECT distinct t1.id,t1.user_id as userId,t1.name,t3.name AS areaName,t1.change_time as changeTime ,t1.intention,t1.`type`,t1.customer_type AS customerType,t4.name AS userName,t1.use_type as useType,t1.progress_num AS progressNum,t1.create_time as createTime
FROM customer t1
LEFT JOIN customer_contact t2 ON t1.id=t2.customer_id
LEFT JOIN `area` t3 ON t1.area_id=t3.Id
LEFT JOIN users t4 ON t1.user_id=t4.id
LEFT JOIN customer_progress t5 ON t1.id=t5.customer_id
where t1.status=0 and t1.customer_type=1

  and t1.change_time=#{param1.changeTime}


  and t1.create_time between #{param1.startDate} and #{param1.endDate}


  and t1.lats_time= #{param1.lastDate}


  and t1.progress_num=#{param1.progressNum}


  and t2.phone=#{param1.phone}


  and t1.intention=#{param1.intention}


  and t1.type=#{param1.type}


  and t3.id=#{param1.areaId}


  and t1.name like concat('%',#{param1.name},'%')


  and t1.use_type = #{param1.useType}


  and t1.group_id in

    #{item,jdbcType=INTEGER}


order by t1.create_time desc

select.if:
select DATE_FORMAT(create_time, "%Y-%m") AS `date`, COUNT(1) as `count` from customer_progress where DATE_FORMAT(create_time,"%Y")=#{year}

  and  user_id in

    #{item,jdbcType=INTEGER}


GROUP BY DATE_FORMAT(create_time, "%Y-%m")

select DATE_FORMAT(create_time, "%Y-%m-%d") AS `date`, COUNT(1) as `count` from customer_progress where DATE_FORMAT(create_time,"%Y-%m")=#{month}

  and  user_id in

    #{item,jdbcType=INTEGER}


GROUP BY DATE_FORMAT(create_time, "%Y-%m-%d")

